import React from "react";

function BotCard({ bot, addToArmy, removeFromArmy, dischargeBot }) {
  const handleAddToArmy = () => {
    addToArmy(bot);
  };

  const handleRemoveFromArmy = () => {
    removeFromArmy(bot.id);
  };

  const handleDischargeBot = () => {
    dischargeBot(bot.id);
  };

  return (
    <div className="ui column">
      <div className="ui card">
        <div className="image">
          <img alt="oh no!" src={bot.avatar_url} />
        </div>
        <div className="content">
          <div className="header">{bot.name}</div>
          <div className="meta text-wrap">
            <small>{bot.catchphrase}</small>
          </div>
        </div>
        <div className="extra content">
          <span>
            <button className="ui mini green button" onClick={handleAddToArmy}>
              Add to Army
            </button>
            <button
              className="ui mini red button"
              onClick={handleRemoveFromArmy}
            >
              Release
            </button>
            <button className="ui mini red button" onClick={handleDischargeBot}>
              Discharge
            </button>
          </span>
        </div>
      </div>
    </div>
  );
}

export default BotCard;
